import React from 'react';
import { NavLink } from 'react-router-dom';

const SimpleNav = () => {
  console.log('SimpleNav component is rendering');

  // Debug info
  const debugInfo = {
    reactVersion: React.version,
    hasRouter: !!window.ReactRouter,
    location: window.location,
    hasHistory: !!window.history,
    hasLocation: !!window.location,
    hasDocument: typeof document !== 'undefined'
  };

  console.log('SimpleNav debug info:', debugInfo);

  // Create some test content that doesn't depend on React Router
  const testContent = (
    <div style={{
      backgroundColor: '#2c3e50',
      color: 'white',
      padding: '1rem',
      borderRadius: '8px',
      margin: '1rem 0',
      textAlign: 'center'
    }}>
      <h3>TEST NAVIGATION</h3>
      <p>This is a test of the navigation system.</p>
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        gap: '1rem',
        marginTop: '1rem'
      }}>
        <a 
          href="/senior"
          style={{
            color: 'white',
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            backgroundColor: '#3498db',
            borderRadius: '4px',
            minWidth: '150px',
            textAlign: 'center',
            fontWeight: 'bold'
          }}
        >
          Senior (Test Link)
        </a>
        <a 
          href="/junior"
          style={{
            color: 'white',
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            backgroundColor: '#2ecc71',
            borderRadius: '4px',
            minWidth: '150px',
            textAlign: 'center',
            fontWeight: 'bold'
          }}
        >
          Junior (Test Link)
        </a>
      </div>
    </div>
  );

  return (
    <div style={{
      backgroundColor: '#2c3e50',
      padding: '1rem',
      margin: '1rem 0',
      borderRadius: '8px',
      textAlign: 'center'
    }}>
      <h3 style={{ color: 'white', margin: '0 0 1rem 0' }}>Syllabus Navigation</h3>
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        gap: '1rem',
        flexWrap: 'wrap'
      }}>
        <NavLink 
          to="/senior"
          style={{
            color: 'white',
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            backgroundColor: '#3498db',
            borderRadius: '4px',
            minWidth: '150px',
            textAlign: 'center',
            fontWeight: 'bold'
          }}
        >
          Senior Syllabus
        </NavLink>
        <NavLink 
          to="/junior"
          style={{
            color: 'white',
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            backgroundColor: '#2ecc71',
            borderRadius: '4px',
            minWidth: '150px',
            textAlign: 'center',
            fontWeight: 'bold'
          }}
        >
          Junior Syllabus
        </NavLink>
      </div>
    </div>
  );
};

export default SimpleNav;
