import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PublicBeltSection from './components/PublicBeltSection';
import './PublicApp.css';

function PublicApp() {
  const [syllabus, setSyllabus] = useState({});
  const [cumulativeSyllabus, setCumulativeSyllabus] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedBelt, setSelectedBelt] = useState('all');
  const [expandedBelts, setExpandedBelts] = useState({});

  // Toggle expansion state for a belt section
  const toggleBeltExpand = (belt) => {
    setExpandedBelts(prev => ({
      ...prev,
      [belt]: !prev[belt]
    }));
  };

  // Initialize all belts as expanded by default
  useEffect(() => {
    if (Object.keys(syllabus).length > 0 && Object.keys(expandedBelts).length === 0) {
      const initialExpandedState = {};
      Object.keys(syllabus).forEach(belt => {
        initialExpandedState[belt] = true; // Start with all belts expanded
      });
      setExpandedBelts(initialExpandedState);
    }
  }, [syllabus]);

  // Belt progression order for cumulative display
  const beltOrder = [
    'Red Belt',
    'Yellow Belt', 
    'Orange Belt',
    'Green Belt',
    'Purple Belt',
    'Blue Belt',
    'Brown Belt',
    'Brown/Black Stripe',
    'Black Belt'
  ];

  useEffect(() => {
    fetchSyllabus();
  }, []);

  // Create cumulative syllabus where each belt includes all previous techniques
  const createCumulativeSyllabus = (syllabusData) => {
    console.log('Creating cumulative syllabus with data:', Object.keys(syllabusData || {}));
    const cumulative = {};
    let allPreviousItems = new Map(); // Track all items we've seen so far

    try {
      // Process each belt in order
      beltOrder.forEach((belt, index) => {
        if (!syllabusData || !syllabusData[belt]) {
          return;
        }

        console.log(`\n=== Processing belt: ${belt} ===`);
        const currentBeltData = {};

        // Process each category in this belt
        Object.entries(syllabusData[belt]).forEach(([category, items]) => {
          if (!Array.isArray(items)) {
            console.warn(`Skipping non-array category: ${category} in ${belt}`);
            return;
          }

          // Initialize category array if it doesn't exist
          if (!currentBeltData[category]) {
            currentBeltData[category] = [];
          }

          console.log(`\nProcessing ${items.length} items in category: ${category}`);

          // Process each item in the category
          items.forEach((item, index) => {
            if (!item) {
              console.warn(`Skipping null/undefined item at index ${index} in ${belt}/${category}`);
              return;
            }

            // Ensure item has required fields
            const safeItem = {
              ...item,
              traditional: item.traditional || '',
              english: item.english || '',
              isCurrentBelt: true,
              _originBelt: belt
            };

            // Create a unique key for this item
            const itemKey = `${safeItem.traditional}_${safeItem.english}_${category}`.toLowerCase();

            // Skip if we've seen this exact item before in a previous belt
            if (allPreviousItems.has(itemKey)) {
              console.log(`Skipping duplicate item: ${itemKey}`);
              return;
            }

            // Mark as seen
            allPreviousItems.set(itemKey, true);

            // Add to current belt's data
            currentBeltData[category].push(safeItem);

            console.log(`Added item: ${safeItem.english || 'Unnamed item'}`);
          });

          console.log(`Category ${category}: ${currentBeltData[category].length} items`);
        });

        // For the first belt, ensure we have all categories even if empty
        if (belt === beltOrder[0]) {
          Object.keys(syllabusData[belt]).forEach(category => {
            if (!currentBeltData[category]) {
              currentBeltData[category] = [];
            }
          });
        }

        // Save the complete data for this belt
        cumulative[belt] = { ...currentBeltData };

        // Debug: Count current belt items
        const currentBeltItemCount = Object.values(currentBeltData).reduce(
          (total, items) => total + (items || []).length, 0
        );

        console.log(`\nBelt ${belt} summary:`, {
          categories: Object.keys(currentBeltData),
          totalItems: currentBeltItemCount,
          categoriesWithItems: Object.entries(currentBeltData)
            .filter(([_, items]) => items && items.length > 0)
            .map(([cat, items]) => `${cat} (${items.length} items)`)
        });
      });

      console.log('\n=== Cumulative syllabus creation complete ===', {
        belts: Object.keys(cumulative),
        totalUniqueItems: allPreviousItems.size,
        itemsPerBelt: Object.fromEntries(
          Object.entries(cumulative).map(([belt, categories]) => [
            belt,
            Object.values(categories).reduce((total, items) => total + (items?.length || 0), 0)
          ])
        )
      });

      return cumulative;

    } catch (error) {
      console.error('Error in createCumulativeSyllabus:', error);
      console.error('Error details:', {
        message: error.message,
        stack: error.stack
      });
      // Return the best effort result even if there was an error
      return cumulative;
    }
  };

  const fetchSyllabus = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/syllabus');
      const syllabusData = response.data;
      setSyllabus(syllabusData);

      // Create and set cumulative syllabus data
      const cumulative = createCumulativeSyllabus(syllabusData);
      setCumulativeSyllabus(cumulative);
    } catch (error) {
      console.error('Error fetching syllabus:', error);
      setError('Error loading syllabus data');
    } finally {
      setLoading(false);
    }
  };

  const filteredSyllabus = () => {
    let filtered = syllabus;

    // Filter by selected belt
    if (selectedBelt !== 'all') {
      filtered = { [selectedBelt]: syllabus[selectedBelt] };
    }

    // Filter by search term
    if (searchTerm.trim()) {
      const searchLower = searchTerm.toLowerCase();
      const result = {};

      Object.entries(filtered).forEach(([belt, categories]) => {
        const filteredCategories = {};
        let hasMatch = false;

        Object.entries(categories).forEach(([category, items]) => {
          const filteredItems = items.filter(item => {
            if (item.traditional && item.traditional.toLowerCase().includes(searchLower)) return true;
            if (item.english && item.english.toLowerCase().includes(searchLower)) return true;
            if (item.name && item.name.toLowerCase().includes(searchLower)) return true;
            if (item.requirements && item.requirements.some(req => req.toLowerCase().includes(searchLower))) return true;
            return false;
          });

          if (filteredItems.length > 0) {
            filteredCategories[category] = filteredItems;
            hasMatch = true;
          }
        });

        if (hasMatch) {
          result[belt] = filteredCategories;
        }
      });

      return result;
    }

    return filtered;
  };

  if (loading) {
    return (
      <div className="public-container">
        <div className="loading">Loading syllabus...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="public-container">
        <div className="error">{error}</div>
      </div>
    );
  }

  const beltOptions = Object.keys(syllabus);
  const filteredData = filteredSyllabus();

  return (
    <div className="public-container">
      <header className="public-header">
        <div className="header-content">
          <img src="/pkr_logo.webp" alt="PKR Logo" className="logo" />
          <div className="header-text">
            <h1>Karate Belt Grading Requirements</h1>
            <p>Progressive Shotokan Syllabus</p>
          </div>
        </div>
      </header>

      <div className="controls">
        <div className="search-section">
          <input
            type="text"
            placeholder="Search techniques, kata, or requirements..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>

        <div className="filter-section">
          <label htmlFor="belt-filter">Filter by Belt:</label>
          <select
            id="belt-filter"
            value={selectedBelt}
            onChange={(e) => setSelectedBelt(e.target.value)}
            className="belt-filter"
          >
            <option value="all">All Belts</option>
            {beltOptions.map(belt => (
              <option key={belt} value={belt}>{belt}</option>
            ))}
          </select>
        </div>
      </div>

      {Object.keys(filteredData).length === 0 ? (
        <div className="no-results">
          <p>No results found for "{searchTerm}"</p>
          <button onClick={() => setSearchTerm('')} className="btn btn-primary">
            Clear Search
          </button>
        </div>
      ) : (
        <div className="syllabus-content">
          {beltOrder.map((belt) => {
            if (!syllabus[belt]) return null;

            return (
              <PublicBeltSection 
                key={belt}
                belt={belt} 
                categories={syllabus[belt]}
                cumulativeCategories={cumulativeSyllabus[belt] || {}}
                isExpanded={expandedBelts[belt]}
                onToggleExpand={() => toggleBeltExpand(belt)}
                searchTerm={searchTerm}
                isVisible={selectedBelt === 'all' || selectedBelt === belt}
              />
            );
          })}
        </div>
      )}

      <footer className="public-footer">
        <p> Peninsula Karate 2025</p>
        <p>
          <a href="/admin" className="admin-link">Admin Access</a>
        </p>
      </footer>
    </div>
  );
}

export default PublicApp;
