import React from 'react';
import { NavLink } from 'react-router-dom';

const SimpleNav = () => {
  console.log('SimpleNav component is rendering');

  return (
    <div style={{
      backgroundColor: '#2c3e50',
      padding: '1rem',
      margin: '1rem 0',
      borderRadius: '8px',
      textAlign: 'center'
    }}>
      <h3 style={{ color: 'white', margin: '0 0 1rem 0' }}>Syllabus Navigation</h3>
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        gap: '1rem',
        flexWrap: 'wrap'
      }}>
        <NavLink 
          to="/senior"
          style={{
            color: 'white',
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            backgroundColor: '#3498db',
            borderRadius: '4px',
            minWidth: '150px',
            textAlign: 'center',
            fontWeight: 'bold'
          }}
        >
          Senior Syllabus
        </NavLink>
        <NavLink 
          to="/junior"
          style={{
            color: 'white',
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            backgroundColor: '#2ecc71',
            borderRadius: '4px',
            minWidth: '150px',
            textAlign: 'center',
            fontWeight: 'bold'
          }}
        >
          Junior Syllabus
        </NavLink>
      </div>
    </div>
  );
};

export default SimpleNav;
