import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
// import Navigation from './components/Navigation';
import SimpleNav from './components/SimpleNav';
import PublicApp from './PublicApp';
import JuniorSyllabus from './JuniorSyllabus';
import './App.css';

function App() {
  // Log render cycle
  console.log('=== APP RENDER START ===');

  // Log environment and dependencies
  console.group('Environment Info');
  console.log('React version:', React.version);
  try {
    const routerInfo = require('react-router-dom');
    console.log('React Router version:', routerInfo.version || 'Unknown version');
    console.log('Router components:', Object.keys(routerInfo).filter(key => typeof routerInfo[key] === 'function'));
  } catch (routerError) {
    console.error('Error loading React Router:', routerError);
  }

  // Log component info
  console.group('Component Info');
  try {
    console.log('SimpleNav:', typeof SimpleNav);
    console.log('PublicApp:', typeof PublicApp);
    console.log('JuniorSyllabus:', typeof JuniorSyllabus);
  } catch (error) {
    console.error('Error checking components:', error);
  }
  console.groupEnd();

  // Log window and document
  console.group('Browser Environment');
  console.log('Window:', typeof window !== 'undefined' ? 'Available' : 'Not available');
  console.log('Document:', typeof document !== 'undefined' ? 'Available' : 'Not available');
  console.log('Location:', window.location ? window.location.href : 'Not available');
  console.groupEnd();

  // Create a simple test element to verify rendering
  const testElement = document.createElement('div');
  testElement.id = 'test-element-' + Date.now();
  testElement.textContent = 'Test Element ' + new Date().toISOString();
  testElement.style.position = 'fixed';
  testElement.style.top = '10px';
  testElement.style.right = '10px';
  testElement.style.backgroundColor = 'red';
  testElement.style.color = 'white';
  testElement.style.padding = '10px';
  testElement.style.zIndex = '9999';

  // Try to append the test element to the body
  try {
    if (document && document.body) {
      document.body.appendChild(testElement);
      console.log('Test element appended to body');

      // Add a style tag to ensure our debug styles are applied
      const style = document.createElement('style');
      style.textContent = `
        .debug-outline {
          outline: 3px solid #f39c12 !important;
          outline-offset: 2px;
          position: relative;
        }
        .debug-outline::after {
          content: attr(data-debug);
          position: absolute;
          top: -20px;
          left: 0;
          background: #f39c12;
          color: white;
          font-size: 12px;
          padding: 2px 5px;
          white-space: nowrap;
          z-index: 10000;
        }
      `;
      document.head.appendChild(style);
      console.log('Debug styles added');
    } else {
      console.error('document.body is not available');
    }
  } catch (error) {
    console.error('Failed to append test element or add styles:', error);
  }

  // Create a test component that doesn't depend on any external components
  const TestNavigation = () => {
    // Log when this component renders
    console.log('TestNavigation is rendering');

    // Check if we can access the DOM
    const canUseDOM = typeof window !== 'undefined' && window.document && window.document.createElement;
    console.log('TestNavigation canUseDOM:', canUseDOM);

    return (
      <div 
        className="debug-outline"
        data-debug="TestNavigation Container"
        style={{
          backgroundColor: '#9b59b6',
          color: 'white',
          padding: '1.5rem',
          margin: '1rem 0',
          borderRadius: '8px',
          textAlign: 'center',
          border: '3px solid #8e44ad',
          boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
          position: 'relative',
          zIndex: 1001
        }}
      >
        <h2 style={{ marginTop: 0, color: 'white' }}>TEST NAVIGATION</h2>
        <p>This is a direct test of navigation rendering.</p>
        <div style={{ 
          position: 'absolute', 
          top: '5px', 
          right: '5px', 
          fontSize: '10px',
          backgroundColor: 'rgba(0,0,0,0.3)',
          padding: '2px 5px',
          borderRadius: '3px',
          color: 'white'
        }}>
          {canUseDOM ? 'DOM Available' : 'No DOM Access'}
        </div>
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '1rem',
          flexWrap: 'wrap',
          marginTop: '1rem'
        }}>
          <a 
            href="/senior"
            style={{
              color: 'white',
              textDecoration: 'none',
              padding: '0.75rem 1.5rem',
              backgroundColor: '#3498db',
              borderRadius: '6px',
              minWidth: '180px',
              textAlign: 'center',
              fontWeight: 'bold',
              fontSize: '1.1rem',
              boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
              transition: 'all 0.2s ease',
              border: '2px solid #2980b9'
            }}
            onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
            onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}
          >
            Senior Syllabus
          </a>
          <a 
            href="/junior"
            style={{
              color: 'white',
              textDecoration: 'none',
              padding: '0.75rem 1.5rem',
              backgroundColor: '#2ecc71',
              borderRadius: '6px',
              minWidth: '180px',
              textAlign: 'center',
              fontWeight: 'bold',
              fontSize: '1.1rem',
              boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
              transition: 'all 0.2s ease',
              border: '2px solid #27ae60'
            }}
            onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
            onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}
          >
            Junior Syllabus
          </a>
        </div>
      </div>
    );
  };

  // Log render start of the main app
  console.log('Rendering main app content');

  return (
    <Router>
      <div className="app" style={{ position: 'relative' }}>
        <header 
          className="app-header debug-outline" 
          data-debug="App Header"
          style={{ 
            position: 'relative', 
            zIndex: 1000,
            backgroundColor: '#2c3e50',
            padding: '1rem 2rem',
            color: 'white'
          }}
        >
          <h1>Karate Syllabus</h1>
          <div style={{ 
            border: '4px solid #e74c3c',
            padding: '20px',
            margin: '20px 0',
            backgroundColor: 'rgba(255, 0, 0, 0.2)',
            borderRadius: '8px',
            boxShadow: '0 0 15px rgba(231, 76, 60, 0.5)'
          }}>
            <h2 style={{ color: '#e74c3c', marginTop: 0 }}>NAVIGATION DEBUG</h2>
            <TestNavigation />
            <div style={{ marginTop: '2rem' }}>
              <SimpleNav />
            </div>
          </div>
        </header>

        <main className="app-content">
          <Routes>
            <Route path="/" element={<Navigate to="/senior" replace />} />
            <Route path="/senior" element={<PublicApp syllabusType="senior" />} />
            <Route path="/junior" element={<JuniorSyllabus />} />
          </Routes>
        </main>

        <footer className="app-footer">
          <p>Â© {new Date().getFullYear()} Karate School. All rights reserved.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
