import React from 'react';
import App from './App';
import PublicApp from './PublicApp';

// Simple test component to verify routing
const TestRouting = () => {
  return (
    <div style={{
      backgroundColor: 'blue',
      color: 'white',
      padding: '15px',
      margin: '10px 0',
      textAlign: 'center',
      fontSize: '18px',
      borderRadius: '5px'
    }}>
      ROUTER IS WORKING - Current Path: {window.location.pathname}
    </div>
  );
};

const Router = () => {
  console.log('Router rendering, path:', window.location.pathname);

  // For now, always render the App component with our navigation
  // We'll restore the routing logic after we fix the navigation
  return (
    <>
      <TestRouting />
      <App />
    </>
  );
};

export default Router;
