import React from 'react';
import App from './App';
import PublicApp from './PublicApp';

// Simple test component to verify routing
const TestRouting = () => {
  return (
    <div style={{
      backgroundColor: 'blue',
      color: 'white',
      padding: '15px',
      margin: '10px 0',
      textAlign: 'center',
      fontSize: '18px',
      borderRadius: '5px'
    }}>
      ROUTER IS WORKING - Current Path: {window.location.pathname}
    </div>
  );
};

const Router = () => {
  console.log('Router rendering, path:', window.location.pathname);

  // Simple routing based on URL path
  const path = window.location.pathname;

  // Wrap all routes with our test component
  const wrapWithTest = (Component) => (
    <>
      <TestRouting />
      <Component />
    </>
  );

  // Admin interface
  if (path === '/admin' || path.startsWith('/admin/')) {
    console.log('Rendering admin interface');
    return wrapWithTest(App);
  }

  // Public interface (default)
  console.log('Rendering public interface');
  return wrapWithTest(PublicApp);
};

export default Router;
