import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navigation from './components/Navigation';
import PublicApp from './PublicApp';
import JuniorSyllabus from './JuniorSyllabus';
import './App.css';

function App() {
  console.log('App component is rendering');

  // Log any potential errors with Navigation import
  try {
    console.log('Navigation component:', Navigation);
  } catch (error) {
    console.error('Error with Navigation component:', error);
  }

  return (
    <Router>
      <div className="app">
        <header className="app-header" style={{ position: 'relative', zIndex: 1000 }}>
          <h1>Karate Syllabus</h1>
          <div style={{ 
            border: '2px solid red',
            padding: '10px',
            margin: '10px 0',
            backgroundColor: 'rgba(255, 0, 0, 0.1)'
          }}>
            <Navigation />
          </div>
        </header>

        <main className="app-content">
          <Routes>
            <Route path="/" element={<Navigate to="/senior" replace />} />
            <Route path="/senior" element={<PublicApp syllabusType="senior" />} />
            <Route path="/junior" element={<JuniorSyllabus />} />
          </Routes>
        </main>

        <footer className="app-footer">
          <p>Â© {new Date().getFullYear()} Karate School. All rights reserved.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
