import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Admin.css';

function Admin() {
  const navigate = useNavigate();

  return (
    <div className="admin-container">
      <h1>Admin Dashboard</h1>
      <div className="admin-actions">
        <button 
          className="btn btn-primary" 
          onClick={() => navigate('/senior')}
        >
          Edit Senior Syllabus
        </button>
        <button 
          className="btn btn-primary" 
          onClick={() => navigate('/junior')}
        >
          Edit Junior Syllabus
        </button>
      </div>
      <div className="admin-section">
        <h2>Backup & Restore</h2>
        <p>Manage syllabus backups and restore previous versions.</p>
        <button className="btn btn-secondary">Create Backup</button>
        <button className="btn btn-secondary">Restore from Backup</button>
      </div>
      <div className="admin-section">
        <h2>User Management</h2>
        <p>Manage admin users and permissions.</p>
        <button className="btn btn-secondary">Add Admin User</button>
      </div>
    </div>
  );
}

export default Admin;
